let icon = document.querySelector('.open-btn');

let animation = bodymovin.loadAnimation({
container: icon,
renderer: 'svg',
loop: false,
autoplay: false,
animationData: data
});
animation.setSpeed(1.5);


let direction = 1;
icon.addEventListener('click', (e) => {
animation.setDirection(direction);
animation.play();
let left;
if(direction === 1) {
left = '0vw)';
} else {
left = '-100vw)';
}
document.getElementById('sidenav-content').style.transform = 'translateX(' + left;
document.getElementById('sidenav-bg').style.transform = 'translateX(' + left;
direction = -direction;
});

//Show info

function showInfo() {
    var infoElement = document.getElementById('info');
    infoElement.style.transform = 'translateX(0) translateY(0) scale(1)';
    document.getElementById('info-btn').style.display = 'none';
}

//Close info

function closeInfo() {
    var infoElement = document.getElementById('info');
    infoElement.style.transform = 'translateX(100vw) translateY(-100vh) scale(0)';
    document.getElementById('info-btn').style.display = 'block';
}
  
// Colour animation of hexagons 
setInterval(() => {
  var hexagons = document.getElementsByClassName('hexagon');
  for(let hex of hexagons) {
    hex.style.backgroundColor = 'rgba(0,0,0,0.8)';
  }
  hexagons[Math.floor(Math.random()*9)].style.backgroundColor = '#211D70';
  hexagons[Math.floor(Math.random()*9)].style.backgroundColor = '#211D70';
  hexagons[Math.floor(Math.random()*9)].style.backgroundColor = '#211D70';
}, 3000);

// Google Sign In
var googleUser = {};
var startApp = function() {
gapi.load('auth2', function(){
  // Retrieve the singleton for the GoogleAuth library and set up the client.
  auth2 = gapi.auth2.init({
  client_id: '1026579868783-ft78qs6p4d6e6og3ofr2clfufi7bgsuc.apps.googleusercontent.com',
  cookiepolicy: 'single_host_origin',
  // Request scopes in addition to 'profile' and 'email'
  //scope: 'additional_scope'
  });
  attachSignin(document.getElementById('proceed-btn'));
  });
};

function attachSignin(element) {
  auth2.attachClickHandler(element, {},
  function(googleUser) {
  console.log("Signed in: " + googleUser.getBasicProfile().getName());
  console.log("Signed in: " + googleUser.getBasicProfile().getEmail());
  window.location.href = 'https://nikhilphalange.github.io/Wiser/loggedIn.html';
  }, function(error) {
  alert(JSON.stringify(error, undefined, 2));
  });
}
